import React, { useState } from "react";
import styles from "./NewUser.module.css";
import Container from "./UI/Container";
import RenderUser from "./RenderUser";

const NewUser = () => {
  const [username, setUsername] = useState("");
  const [age, setAge] = useState("");

  const logUsername = (event) => {
    setUsername(event.target.value);
  };
  const logAge = (event) => {
    setAge(event.target.value);
  };

  const sendData = (event) => {
    event.preventDefault();
    const userData = {
      username: username,
      age: age,
    };
  };
  return (
    <>
      <Container>
        <label className={styles.username} for="username">
          Username
        </label>
        <input
          className={styles["username-field"]}
          onChange={logUsername}
          type="text"
          id="username"
        ></input>
        <label className={styles.age} for="age">
          Age (Years)
        </label>
        <input
          className={styles["age-field"]}
          type="number"
          id="age"
          onChange={logAge}
        ></input>

        <input
          className={styles.submit}
          type="button"
          value="Add user"
          onClick={sendData}
        ></input>
      </Container>
      <RenderUser userData={userData}></RenderUser>
    </>
  );
};

export default NewUser;
